import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [mood, setMood] = useState('');
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [inputMethod, setInputMethod] = useState('demo'); // 'demo', 'webcam', or 'upload'

  const fetchRecommendations = async (method, data = null) => {
    try {
      setLoading(true);
      setError('');

      let response;
      const endpoint = 'http://localhost:5000/api/recommendations';

      if (method === 'demo') {
        response = await fetch(endpoint);
      } 
      else if (method === 'webcam') {
        response = await fetch(`${endpoint}?mood=webcam`);
      }
      else if (method === 'upload' && data) {
        const formData = new FormData();
        formData.append('file', data);
        response = await fetch(endpoint, {
          method: 'POST',
          body: formData
        });
      }

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const result = await response.json();
      if (result.status !== 'success') {
        throw new Error(result.message || 'Unknown error');
      }

      setMood(result.mood);
      setSongs(result.recommended_songs || []);

    } catch (err) {
      console.error('Fetch error:', err);
      setError(err.message);
      setSongs([]);
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setInputMethod('upload');
      fetchRecommendations('upload', file);
    }
  };

  const playSong = (song) => {
    if (song.spotify_id) {
      window.open(`https://open.spotify.com/track/${song.spotify_id}`, '_blank');
    } 
    else if (song.link) {
      window.open(song.link, '_blank');
    } 
    else {
      // Fallback to search
      window.open(
        `https://open.spotify.com/search/${encodeURIComponent(`${song.name} ${song.artist}`)}`,
        '_blank'
      );
    }
  };

  return (
    <div className="app-container">
      <nav className="navbar navbar-dark bg-primary">
        <div className="container">
          <span className="navbar-brand">Emotify Music</span>
        </div>
      </nav>

      <main className="container mt-4">
        <div className="text-center mb-4">
          <h1>Mood-Based Music Recommendations</h1>
          <p className="lead">Discover songs that match your emotion</p>
        </div>

        <div className="control-panel card mb-4">
          <div className="card-body">
            <div className="btn-group w-100 mb-3">
              <button
                className={`btn ${inputMethod === 'demo' ? 'btn-primary' : 'btn-outline-primary'}`}
                onClick={() => {
                  setInputMethod('demo');
                  fetchRecommendations('demo');
                }}
                disabled={loading}
              >
                Demo Mode
              </button>
              <button
                className={`btn ${inputMethod === 'webcam' ? 'btn-primary' : 'btn-outline-primary'}`}
                onClick={() => {
                  setInputMethod('webcam');
                  fetchRecommendations('webcam');
                }}
                disabled={loading}
              >
                Use Webcam
              </button>
              <label className={`btn ${inputMethod === 'upload' ? 'btn-primary' : 'btn-outline-primary'}`}>
                Upload Image
                <input 
                  type="file" 
                  accept="image/*" 
                  onChange={handleImageUpload}
                  style={{display: 'none'}}
                  disabled={loading}
                />
              </label>
            </div>

            {loading && (
              <div className="text-center">
                <div className="spinner-border text-primary"></div>
                <p>Analyzing your mood...</p>
              </div>
            )}
          </div>
        </div>

        {error && (
          <div className="alert alert-danger">
            <strong>Error:</strong> {error}
            {error.includes('Failed to fetch') && (
              <div>Please make sure the backend server is running on port 5000</div>
            )}
          </div>
        )}

        {mood && (
          <div className="mood-display mb-4">
            <h2 className="text-center">
              Detected Mood: <span className="badge bg-info">{mood}</span>
            </h2>
          </div>
        )}

        <div className="song-recommendations">
          {songs.length > 0 ? (
            <div className="row">
              {songs.map((song, index) => (
                <div key={index} className="col-md-4 mb-4">
                  <div className="card h-100">
                    <div className="card-body text-center">
                      <h5 className="card-title">{song.name}</h5>
                      <p className="card-text text-muted">{song.artist}</p>
                    </div>
                    <div className="card-footer text-center bg-transparent">
                      <button
                        onClick={() => playSong(song)}
                        className="btn btn-success"
                      >
                        Play on Spotify
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            !loading && (
              <div className="alert alert-info text-center">
                No songs found. Try detecting your mood again.
              </div>
            )
          )}
        </div>
      </main>

      <footer className="bg-dark text-white text-center py-3 mt-4">
        <p>© {new Date().getFullYear()} Emotify Music</p>
      </footer>
    </div>
  );
}

export default App;
