import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Bgm from './components/bgm.mp4';

function Garlic() {
    const [mood, setMood] = useState('');
    const [songs, setSongs] = useState([]);

    useEffect(() => {
        // Fetch song recommendations from backend
        fetch('http://127.0.0.1:5000/recommendations')
            .then(response => response.json())
            .then(data => {
                setMood(data.mood); // Detected mood
                setSongs(data.recommended_songs || []); // Song list
            })
            .catch(error => console.error('Error fetching recommendations:', error));
    }, []);

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-dark" style={{ paddingLeft: "20px" }}>
                <h1 className="navbar-brand logo" href="#">ANKH</h1>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item active">
                            <a className="nav-link" id="home" href="http://localhost:3000/home">Home</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" id="abt-us" href="http://localhost:3000/about">About-Us</a>
                        </li>
                    </ul>
                </div>
            </nav>
            <video loop autoPlay muted id="myvideo">
                <source src={Bgm}></source>
            </video>

            <div>
                <h1 id="abt">Introducing Emotify</h1>
                <p id="abt2">
                    <center>
                        Weâ€™ve trained a model called Emotify which reads facial expressions <br />
                        and plays songs to lighten the mood.
                    </center>
                </p>
            </div>
            <a href="http://127.0.0.1:5000/" id="abt3" target="_blank">Try Emotify</a>

            {/* ðŸŽµ Mood Detection */}
            <div className="container mt-4">
                <h2>Detected Mood: {mood || "Loading..."}</h2>

                {/* ðŸŽ¶ Display Recommended Songs */}
                {songs.length > 0 ? (
                    <div>
                        <h3>Recommended Songs</h3>
                        <div className="row">
                            {songs.map((song, index) => (
                                <div key={index} className="col-md-4 mb-4">
                                    <div className="card shadow-sm">
                                        <img 
                                            src={`https://via.placeholder.com/300?text=${encodeURIComponent(song.name)}`} 
                                            alt={song.name} 
                                            className="card-img-top"
                                        />
                                        <div className="card-body">
                                            <h5 className="card-title">{song.name}</h5>
                                            <p className="card-text">{song.artist}</p>
                                            <a 
                                                href={`https://open.spotify.com/search/${encodeURIComponent(song.name + " " + song.artist)}`} 
                                                target="_blank"
                                                rel="noopener noreferrer"
                                                className="btn btn-success"
                                            >
                                                ðŸŽµ Play on Spotify
                                            </a>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ) : (
                    <p>No songs found for this mood.</p>
                )}
            </div>
        </div>
    );
}

export default Garlic;
