import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [mood, setMood] = useState('');
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showWebcam, setShowWebcam] = useState(false);

  const fetchRecommendations = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch('http://localhost:5000/recommendations');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setMood(data.mood);
      setSongs(data.recommended_songs || []);
    } catch (err) {
      console.error("Fetch error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      setLoading(true);
      setError(null);

      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('http://localhost:5000/recommendations', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setMood(data.mood);
      setSongs(data.recommended_songs || []);
    } catch (err) {
      console.error("Upload error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <nav className="navbar navbar-dark bg-dark">
        <div className="container">
          <span className="navbar-brand mb-0 h1">Emotify</span>
        </div>
      </nav>

      <main className="container mt-4">
        <div className="text-center mb-5">
          <h1 className="display-4">Mood-Based Music Recommendations</h1>
          <p className="lead">Discover songs that match your current emotion</p>
        </div>

        <div className="row justify-content-center mb-4">
          <div className="col-md-8 text-center">
            <div className="btn-group" role="group">
              <button 
                className="btn btn-primary"
                onClick={fetchRecommendations}
                disabled={loading}
              >
                {loading ? 'Detecting...' : 'Use Webcam'}
              </button>

              <label className="btn btn-secondary" style={{cursor: 'pointer'}}>
                Upload Image
                <input 
                  type="file" 
                  accept="image/*" 
                  onChange={handleImageUpload}
                  style={{display: 'none'}}
                  disabled={loading}
                />
              </label>
            </div>
          </div>
        </div>

        {error && (
          <div className="alert alert-danger text-center">
            {error}
          </div>
        )}

        {loading && (
          <div className="text-center my-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p>Analyzing your mood...</p>
          </div>
        )}

        {mood && !loading && (
          <div className="results-section">
            <div className="text-center mb-4">
              <h2>Your Mood: <span className="badge bg-info">{mood}</span></h2>
            </div>

            {songs.length > 0 ? (
              <div className="row">
                {songs.map((song, index) => (
                  <div key={index} className="col-md-4 mb-4">
                    <div className="card h-100">
                      <div className="card-body">
                        <h5 className="card-title">{song.name}</h5>
                        <p className="card-text text-muted">{song.artist}</p>
                      </div>
                      <div className="card-footer bg-transparent">
                        <a 
                          href={`https://open.spotify.com/search/${encodeURIComponent(song.name + " " + song.artist)}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="btn btn-success btn-sm"
                        >
                          Listen on Spotify
                        </a>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="alert alert-warning text-center">
                No songs found for this mood. Please try again.
              </div>
            )}
          </div>
        )}
      </main>

      <footer className="bg-dark text-white text-center py-3 mt-5">
        <p>© 2023 Emotify - Mood-Based Music Recommendations</p>
      </footer>
    </div>
  );
}

export default App;
