import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Bgm from './components/bgm.mp4';

function Garlic() {
    const [mood, setMood] = useState('');
    const [songs, setSongs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchRecommendations = async () => {
            try {
                setLoading(true);
                const response = await fetch('http://127.0.0.1:5000/recommendations');

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log("API Response:", data); // Debug log

                setMood(data.mood);
                setSongs(data.recommended_songs || []);
            } catch (err) {
                console.error("Fetch error:", err);
                setError(err.message);
                setMood("Error detecting mood");
            } finally {
                setLoading(false);
            }
        };

        fetchRecommendations();
    }, []);

    return (
        <div className="garlic-container">
            <nav className="navbar navbar-expand-lg navbar-light bg-dark" style={{ paddingLeft: "20px" }}>
                <h1 className="navbar-brand logo" href="#">ANKH</h1>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item active">
                            <a className="nav-link" id="home" href="http://localhost:3000/home">Home</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" id="abt-us" href="http://localhost:3000/about">About-Us</a>
                        </li>
                    </ul>
                </div>
            </nav>

            <video loop autoPlay muted id="myvideo">
                <source src={Bgm} type="video/mp4"></source>
            </video>

            <div className="intro-section">
                <h1 id="abt">Introducing Emotify</h1>
                <p id="abt2">
                    <center>
                        We've trained a model called Emotify which reads facial expressions <br />
                        and plays songs to lighten the mood.
                    </center>
                </p>
            </div>
            <a href="http://127.0.0.1:5000/" id="abt3" target="_blank" rel="noopener noreferrer">Try Emotify</a>

            {/* Mood and Songs Section */}
            <div className="container mt-4 recommendations-container">
                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border text-primary" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                        <p>Detecting your mood...</p>
                    </div>
                ) : error ? (
                    <div className="alert alert-danger">
                        <strong>Error:</strong> {error}
                    </div>
                ) : (
                    <>
                        <h2>Detected Mood: <span className="mood-text">{mood}</span></h2>

                        {songs.length > 0 ? (
                            <div className="songs-section">
                                <h3>Recommended Songs</h3>
                                <div className="row">
                                    {songs.map((song, index) => (
                                        <div key={index} className="col-md-4 mb-4">
                                            <div className="card shadow-sm h-100">
                                                <img 
                                                    src={`https://via.placeholder.com/300x150?text=${encodeURIComponent(song.name)}`} 
                                                    alt={song.name} 
                                                    className="card-img-top"
                                                />
                                                <div className="card-body">
                                                    <h5 className="card-title">{song.name}</h5>
                                                    <p className="card-text">{song.artist}</p>
                                                    <a 
                                                        href={`https://open.spotify.com/search/${encodeURIComponent(song.name + " " + song.artist)}`} 
                                                        target="_blank"
                                                        rel="noopener noreferrer"
                                                        className="btn btn-success"
                                                    >
                                                        🎵 Play on Spotify
                                                    </a>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ) : (
                            <div className="alert alert-info">
                                No songs found for this mood. Try the Emotify feature to get recommendations!
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
}

export default Garlic;
