import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [mood, setMood] = useState('');
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [spotifyToken, setSpotifyToken] = useState(null);

  // Get Spotify access token (simplified version)
  useEffect(() => {
    const getSpotifyToken = async () => {
      try {
        const response = await fetch('http://localhost:5000/spotify-token');
        const data = await response.json();
        setSpotifyToken(data.access_token);
      } catch (err) {
        console.error("Couldn't get Spotify token:", err);
      }
    };
    getSpotifyToken();
  }, []);

  const fetchRecommendations = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch('http://localhost:5000/recommendations');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setMood(data.mood);
      setSongs(data.recommended_songs || []);
    } catch (err) {
      console.error("Fetch error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const playOnSpotify = (trackUri) => {
    if (trackUri) {
      window.open(`https://open.spotify.com/track/${trackUri.split(':')[2]}`, '_blank');
    }
  };

  return (
    <div className="app-container">
      <nav className="navbar navbar-dark bg-dark">
        <div className="container">
          <span className="navbar-brand mb-0 h1">Emotify</span>
        </div>
      </nav>

      <main className="container mt-4">
        <div className="text-center mb-5">
          <h1 className="display-4">Mood-Based Music Player</h1>
          <p className="lead">Songs that match your current emotion</p>
        </div>

        <div className="text-center mb-4">
          <button 
            className="btn btn-primary btn-lg"
            onClick={fetchRecommendations}
            disabled={loading}
          >
            {loading ? 'Detecting Mood...' : 'Detect My Mood'}
          </button>
        </div>

        {error && (
          <div className="alert alert-danger text-center">
            {error}
          </div>
        )}

        {loading && (
          <div className="text-center my-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p>Analyzing your mood...</p>
          </div>
        )}

        {mood && !loading && (
          <div className="results-section">
            <div className="text-center mb-4">
              <h2>Your Mood: <span className="badge bg-info">{mood}</span></h2>
            </div>

            {songs.length > 0 ? (
              <div className="row">
                {songs.map((song, index) => (
                  <div key={index} className="col-md-4 mb-4">
                    <div className="card h-100">
                      <div className="card-body text-center">
                        <h5 className="card-title">{song.name}</h5>
                        <p className="card-text text-muted">{song.artist}</p>
                        {song.album_cover && (
                          <img 
                            src={song.album_cover} 
                            alt="Album cover" 
                            className="img-fluid mb-3 rounded"
                            style={{maxHeight: '200px'}}
                          />
                        )}
                      </div>
                      <div className="card-footer bg-transparent text-center">
                        <button
                          onClick={() => playOnSpotify(song.uri)}
                          className="btn btn-success btn-sm me-2"
                        >
                          Play on Spotify
                        </button>
                        {spotifyToken && (
                          <button
                            onClick={() => playPreview(song.preview_url)}
                            className="btn btn-primary btn-sm"
                            disabled={!song.preview_url}
                          >
                            Play Preview
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="alert alert-warning text-center">
                No songs found for this mood. Please try again.
              </div>
            )}
          </div>
        )}
      </main>

      <footer className="bg-dark text-white text-center py-3 mt-5">
        <p>© 2023 Emotify - Mood-Based Music Player</p>
      </footer>

      {/* Audio element for preview playback */}
      <audio id="preview-player" controls style={{display: 'none'}}></audio>
    </div>
  );

  function playPreview(previewUrl) {
    if (!previewUrl) return;
    const player = document.getElementById('preview-player');
    player.src = previewUrl;
    player.play();
  }
}

export default App;
